import React, { useContext } from "react";
import Clock from "./Sub-component/Clock";
import { NavLink } from "react-router-dom";
import UserContext from "./Context/UserContext";
export default function Header() {
  const { city, setCity, input, setInput, setLocation } =
    useContext(UserContext);
  function handleEnter(event) {
    if (event.key == "Enter") {
      if (input.trim()) {
        let formattedInput = input.replace(/\s+/g, "+");
        setLocation(formattedInput);
      }
      setCity(
        input.replace(
          /\w\S*/g,
          (text) =>
            text.charAt(0).toUpperCase() + text.substring(1).toLowerCase()
        )
      );
    }
  }
  // Function to format date as "DD MMM"
  function formatDate(date) {
    const options = { day: "2-digit", month: "short" };
    return date.toLocaleDateString("en-GB", options);
  }

  // Function to get future dates
  function getFutureDates(daysAhead) {
    const today = new Date();
    today.setDate(today.getDate() + daysAhead);
    return formatDate(today);
  }

  // Getting the dates for 3, 4, 5 days ahead
  const date3DaysAhead = getFutureDates(2);
  const date4DaysAhead = getFutureDates(3);
  const date5DaysAhead = getFutureDates(4);

  return (
    <>
      <Clock />
      <div className="location">
        <h1>{city}</h1>
        <div className="search-header">
          <input
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={handleEnter}
            placeholder="Search"
            className="search-header__input"
            type="text"
          />
          <button className="search-header__button">
            <svg
              fill="none"
              viewBox="0 0 18 18"
              height="18"
              width="18"
              className="search-header__icon"
              onClick={() => {
                if (input.trim()) {
                  let formattedInput = input.replace(/\s+/g, "+");
                  setLocation(formattedInput);
                }
                setCity(
                  input.replace(
                    /\w\S*/g,
                    (text) =>
                      text.charAt(0).toUpperCase() +
                      text.substring(1).toLowerCase()
                  )
                );
              }}
            >
              <path
                fill="#3A3A3A"
                d="M7.132 0C3.197 0 0 3.124 0 6.97c0 3.844 3.197 6.969 7.132 6.969 1.557 0 2.995-.49 4.169-1.32L16.82 18 18 16.847l-5.454-5.342a6.846 6.846 0 0 0 1.718-4.536C14.264 3.124 11.067 0 7.132 0zm0 .82c3.48 0 6.293 2.748 6.293 6.15 0 3.4-2.813 6.149-6.293 6.149S.839 10.37.839 6.969C.839 3.568 3.651.82 7.132.82z"
              ></path>
            </svg>
          </button>
        </div>
        <ul className="example-2">
          <li className="icon-content">
            <a
              href="https://linkedin.com/in/himaansh-uu"
              aria-label="LinkedIn"
              data-social="linkedin"
            >
              <div className="filled"></div>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-linkedin"
                viewBox="0 0 16 16"
              >
                <path
                  d="M0 1.146C0 .513.526 0 1.175 0h13.65C15.474 0 16 .513 16 1.146v13.708c0 .633-.526 1.146-1.175 1.146H1.175C.526 16 0 15.487 0 14.854zm4.943 12.248V6.169H2.542v7.225zm-1.2-8.212c.837 0 1.358-.554 1.358-1.248-.015-.709-.52-1.248-1.342-1.248S2.4 3.226 2.4 3.934c0 .694.521 1.248 1.327 1.248zm4.908 8.212V9.359c0-.216.016-.432.08-.586.173-.431.568-.878 1.232-.878.869 0 1.216.662 1.216 1.634v3.865h2.401V9.25c0-2.22-1.184-3.252-2.764-3.252-1.274 0-1.845.7-2.165 1.193v.025h-.016l.016-.025V6.169h-2.4c.03.678 0 7.225 0 7.225z"
                  fill="currentColor"
                ></path>
              </svg>
            </a>
            <div className="tooltip">LinkedIn</div>
          </li>
          <li className="icon-content">
            <a
              href="https://github.com/Himaanshuuuu04"
              aria-label="GitHub"
              data-social="github"
            >
              <div className="filled"></div>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-github"
                viewBox="0 0 16 16"
              >
                <path
                  d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27s1.36.09 2 .27c1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.01 8.01 0 0 0 16 8c0-4.42-3.58-8-8-8"
                  fill="currentColor"
                ></path>
              </svg>
            </a>
            <div className="tooltip">GitHub</div>
          </li>
          <li className="icon-content">
            <a
              href="https://www.instagram.com/himaansh_uu/"
              aria-label="Instagram"
              data-social="instagram"
            >
              <div className="filled"></div>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                className="bi bi-instagram"
                viewBox="0 0 16 16"
